<link rel='stylesheet' href='https://drawing.holygrail.com.ua/bast_form.css' type='text/css' media='all' />
<style>
.watermark {
  position: relative;
  text-align: center;
  top: 45%;
  font-size: 80px;
  /* transform-origin: top left; */
  transform: rotate(338deg);
  opacity: 20%;
}
.complect_wrapper *, .full_complect_wrapper * {
  font-family: \'Roboto\', sans-serif;
}

a {
  transition: all .3 ease;
}

.complect_wrapper {
  border-left: 9px solid #009DE0;
  margin-bottom: 14px;
  width: 1181px;
}

.complect_wrapper_title {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 26px;
  background: rgb(229 229 299 / 19%);
}

.complect_wrapper_title p {
  font-size: 14px;
  font-weight: 900;
  color: #000;
}

.complect_wrapper_title a {
  font-size: 14px;
  font-weight: 900;
  color: #009DE0;
  text-decoration: none;
}

.products_block, .full_complect_wrapper {
  display: flex;
}

.product_wrapper {
  border-top: 1px solid #F5F5F5;
  border-right: 1px solid #F5F5F5;
  border-bottom: 1px solid #F5F5F5;
}

.product_wrapper:first-child {
  border-left: 1px solid #F5F5F5;
}

.product_image {
  padding: 15px;
}

.product_image img,
.full_complect_top img {
  width: 100%;
}

.product_title {
  border-top: 1px solid #F5F5F5;
  height: 65px;
}

.product_title a {
  font-size: 14px;
  line-height: 16px;
  font-weight: 900;
  color: #000;
  text-decoration: none;
  margin: 10px 10px 0 21px;
  display: block;
}

.complect_wrapper_price {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  background: #F5F5F5;
}

.complect_wrapper_price-top {
  width: 144px;
  display: flex;
  align-items: center;
  height: 100%;
}

.price_wrapper {
  padding-left: 21px;
}

.price_text {
  display: block;
  font-size: 14px;
  font-weight: 900;
  color: #000;
}

.price {
  font-size: 24px;
  font-weight: 900;
  color: #000;
}

.price_line-through {
  font-size: 12px;
  font-weight: 900;
  color: #000;
  text-decoration: line-through;
}

.price_currency {
  font-size: 12px;
  font-weight: 900;
  color: #000;
}

.complect_wrapper_price-bottom {
  width: 144px;
  height: 100%;
  max-height: 66px;
}

.order_complect_btn {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 2px solid #009DE0;
  background: #fff;
  font-size: 14px;
  color: #009DE0;
  text-transform: uppercase;
  font-weight: 900;
  cursor: pointer;
  transition: all .3s ease;
}

a:hover {
  text-decoration: underline !important;
}

.order_complect_btn:hover {
  background: #009DE0;
  color: #fff;
  box-shadow: 0 2px 10px 0 rgb(0 0 0 / 40%);
}

.order_complect_btn:active {
  background: #009DE0;
  color: #fff;
  box-shadow: none;
}

.full_complect_wrapper {
  gap: 27px;
  margin-bottom: 29px;
  width: 1181px;
}

.full_complect_top {
  border: 1px solid #F5F5F5;
  padding: 15px;
}

.full_complect_bottom {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border-right: 1px solid #F5F5F5;
  border-bottom: 1px solid #F5F5F5;
  border-left: 4px solid #009DE0;
  height: 111px;
  padding: 10px 17px;
}

.full_complect_href a {
  font-size: 12px;
  font-weight: 900;
  color: #000;
  text-transform: uppercase;
  text-decoration: none;
}

.full_complect_price-block {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.cart_block {
  cursor: pointer;
}
</style>